import UIKit
import SnapKit
import Photos

protocol CouponAddDisplayLogic: class {
    func displayFetchedPhotos(viewModel: CouponAdd.fetchPhoto.ViewModel)
    func displayLargeImageView(viewModel: CouponAdd.fetchOnePhoto.ImageViewModel)
    func displayChangeSelectedCell(viewModel: CouponAdd.fetchOnePhoto.CellViewModel)
}

class CouponAddViewController: UIViewController, CouponAddDisplayLogic {

    var interactor: CouponAddBusinessLogic?
    var router: (NSObjectProtocol & CouponAddRoutingLogic & CouponAddDataPassing)?
    
    // MARK: Object lifecycle
    
    override init(nibName nibNameOrNil: String?, bundle nibBundleOrNil: Bundle?) {
        super.init(nibName: nibNameOrNil, bundle: nibBundleOrNil)
        setup()
    }
    
    required init?(coder aDecoder: NSCoder) {
        super.init(coder: aDecoder)
        setup()
    }
    
    // MARK: Setup
    
    private func setup() {
        let viewController = self
        let interactor = CouponAddInteractor()
        let presenter = CouponAddPresenter()
        let router = CouponAddRouter()
        viewController.interactor = interactor
        viewController.router = router
        interactor.presenter = presenter
        presenter.viewController = viewController
        router.viewController = viewController
        router.dataStore = interactor
    }
    
    // MARK: Routing
    
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        if let scene = segue.identifier {
            let selector = NSSelectorFromString("routeTo\(scene)WithSegue:")
            if let router = router, router.responds(to: selector) {
                router.perform(selector, with: segue)
            }
        }
    }
    
    // MARK: View lifecycle
    
    override func viewDidLoad() {
        super.viewDidLoad()
        setupUI()
        setupCollectionView()
        fetchPhotos()
    }
    
    // MARK: fetch Photos
    
    var displayedPhoto: [PHAsset] = []
    
    func fetchPhotos() {
        let request = CouponAdd.fetchPhoto.Request()
        interactor?.fetchPhotos(request: request)
    }
    
    func displayFetchedPhotos(viewModel: CouponAdd.fetchPhoto.ViewModel) {
        displayedPhoto = viewModel.assets
        interactor?.changeSelectedImage(request: .init(prev: selectedIndex, curr: selectedIndex, asset: displayedPhoto[selectedIndex.row]))
        collectionView.reloadData()
    }
    
    // MARK: fetch One Photos
    
    var selectedIndex: IndexPath = IndexPath(row: 0, section: 0)
    
    func displayLargeImageView(viewModel: CouponAdd.fetchOnePhoto.ImageViewModel) {
        DispatchQueue.main.async {
            self.imageView.image = viewModel.image
        }
    }
    
    func displayChangeSelectedCell(viewModel: CouponAdd.fetchOnePhoto.CellViewModel) {
        self.selectedIndex = viewModel.curr
        UIView.performWithoutAnimation {
            self.collectionView.reloadItems(at: [viewModel.prev, viewModel.curr])
        }
    }
    // MARK: View
    
    let header: CouponAddHeader = {
        let header = CouponAddHeader()
        return header
    }()
    
    let imageView: UIImageView = {
        let imageView = UIImageView()
        imageView.contentMode = .scaleAspectFit
        imageView.backgroundColor = .systemBackground
        return imageView
    }()
    
    let collectionView: UICollectionView = {
        let layout = UICollectionViewFlowLayout()
        let collection = UICollectionView(frame: .zero, collectionViewLayout: layout)
        collection.backgroundColor = .systemBackground
        return collection
    }()
}

extension CouponAddViewController {
    
    @objc func xmarkTouched() {
        self.dismiss(animated: true, completion: nil)
    }
    
    @objc func nextIconTouched() {
        router?.routeToCouponScan(index: selectedIndex)
    }
    
    func setupUI() {
        view.backgroundColor = .systemBackground
        [header, imageView, collectionView].forEach {
            view.addSubview($0)
        }
        header.xmarkIcon.addTarget(self, action: #selector(xmarkTouched), for: .touchUpInside)
        header.nextIcon.addTarget(self, action: #selector(nextIconTouched), for: .touchUpInside)
        collectionView.delegate = self
        collectionView.dataSource = self
        
        imageView.snp.makeConstraints { make in
            make.leading.trailing.equalToSuperview()
            make.top.equalTo(header.snp.bottom)
            make.height.equalTo(300)
        }
        
        header.snp.makeConstraints { make in
            make.top.equalTo(view.safeAreaLayoutGuide)
            make.height.equalTo(43)
            make.leading.trailing.equalToSuperview()
        }
        
        collectionView.snp.makeConstraints { make in
            make.leading.trailing.bottom.equalToSuperview()
            make.top.equalTo(imageView.snp.bottom).offset(2)
        }
    }
}

extension CouponAddViewController: UICollectionViewDelegate, UICollectionViewDataSource {
    
    var cellName: String { "PhotoCell" }
    
    func setupCollectionView() {
        collectionView.delegate = self
        collectionView.dataSource = self
        collectionView.alwaysBounceVertical = true
        collectionView.register(PhotoCell.self, forCellWithReuseIdentifier: cellName)
    }
    
    func collectionView(_ collectionView: UICollectionView, numberOfItemsInSection section: Int) -> Int {
        return displayedPhoto.count
    }
    
    func collectionView(_ collectionView: UICollectionView, cellForItemAt indexPath: IndexPath) -> UICollectionViewCell {
        guard let cell = collectionView.dequeueReusableCell(withReuseIdentifier: cellName, for: indexPath) as? PhotoCell else {
            return PhotoCell()
        }
        cell.onReuse = {
            ImageLoader.shared.cancelLoad(self.displayedPhoto[indexPath.row] )
        }
        if selectedIndex.row == indexPath.row {
            cell.setSelectedCell()
        }
        ImageLoader.shared.loadImage(displayedPhoto[indexPath.row]) { image in
            DispatchQueue.main.async {
                try? cell.imageView.image = image.get()
            }
        }

        return cell
    }
    
    func collectionView(_ collectionView: UICollectionView, layout collectionViewLayout: UICollectionViewLayout, insetForSectionAt section: Int) -> UIEdgeInsets {
        return UIEdgeInsets(top: 10, left: 10, bottom: 10, right: 10)
    }
    
    func collectionView(_ collectionView: UICollectionView, didSelectItemAt indexPath: IndexPath) {
        guard indexPath != selectedIndex else { return }
        let request = CouponAdd.fetchOnePhoto.Request(prev: selectedIndex, curr: indexPath, asset: displayedPhoto[indexPath.row])
        interactor?.changeSelectedImage(request: request)
    }
}

extension CouponAddViewController: UICollectionViewDelegateFlowLayout {
    func collectionView(_ collectionView: UICollectionView, layout collectionViewLayout: UICollectionViewLayout, sizeForItemAt indexPath: IndexPath) -> CGSize {
        let w = (view.frame.width - 50) / 4
        return CGSize(width: w, height: w)
    }
}
